    private void jMenuItem1ActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_jMenuItem1ActionPerformed
    {//GEN-HEADEREND:event_jMenuItem1ActionPerformed
        // TODO add your handling code here:
        final JFrame game = new JFrame();
	final GameJPanel panel = new GameJPanel();
	game.add(panel);
	System.out.println("" + panel.getPreferredSize());
        //game.setSize(panel.getPreferredSize());
	game.setSize(325, 388);
	game.setResizable(false);
        game.setVisible(true);
	
	
	WindowListener listener = new WindowAdapter() 
        {
            public void windowClosing(WindowEvent w) 
            {
		System.out.println("" + game.getSize());
                panel.stopGame(BoardModel.Cell.Empty);
            }
        };
        game.addWindowListener(listener);
    }//GEN-LAST:event_jMenuItem1ActionPerformed

