    private void jMenuItem3ActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_jMenuItem3ActionPerformed
    {//GEN-HEADEREND:event_jMenuItem3ActionPerformed
        // TODO add your handling code here:
        Object[] options =
        {
            "Закрыть"
        };
        JOptionPane.showOptionDialog(
            null,
            "Играют два соперника: один чёрными фишками (камнями), другой — белыми.\n"
            + "Игра проходит на расчерченной в клетку доске размером 15 на 15 линий.\n"
            + "Игроки делают ходы по очереди. Первыми ходят белые. Каждым ходом игрок\n"
            + "выставляет на доску в любую свободную клетку доски один камень своего цвета.\n" 
            + "Побеждает тот, кто сможет первым построить непрерывный ряд из пяти камней своего цвета", "Справка",
            JOptionPane.DEFAULT_OPTION,
            JOptionPane.INFORMATION_MESSAGE,
            null, options, options[0]);

    }//GEN-LAST:event_jMenuItem3ActionPerformed

